import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { db } from '../services/supabaseClient';
import { 
  BookOpen, 
  Clock, 
  CheckCircle, 
  Lock, 
  Download, 
  BarChart3,
  Calendar,
  Award,
  FileText,
  Play
} from 'lucide-react';
import { formatDate, calculatePercentage } from '../lib/utils';

const StudentDashboard = () => {
  const { user, profile } = useAuth();
  const [enrolledCourses, setEnrolledCourses] = useState([]);
  const [quizScores, setQuizScores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    loadDashboardData();
  }, [user, loadDashboardData]);

  const loadDashboardData = async () => {
    if (!user) return;

    try {
      setLoading(true);
      
      // Load enrolled courses
      const { data: courses, error: coursesError } = await db.getEnrolledCourses(user.id);
      if (coursesError) throw coursesError;
      
      // Load quiz scores
      const { data: scores, error: scoresError } = await db.getUserQuizScores(user.id);
      if (scoresError) throw scoresError;

      setEnrolledCourses(courses || []);
      setQuizScores(scores || []);
    } catch (err) {
      setError('Failed to load dashboard data');
      console.error('Dashboard loading error:', err);
    } finally {
      setLoading(false);
    }
  };

  const getQuizProgress = (weekNumber) => {
    const quizScore = quizScores.find(score => score.quizzes?.week_number === weekNumber);
    return quizScore ? quizScore.score : null;
  };

  const isQuizLocked = (weekNumber) => {
    // Quiz is locked if previous week's quiz is not completed
    if (weekNumber === 1) return false;
    const previousQuizScore = getQuizProgress(weekNumber - 1);
    return previousQuizScore === null;
  };

  const getOverallProgress = () => {
    const totalQuizzes = 12; // Assuming 12 weeks
    const completedQuizzes = quizScores.length;
    return calculatePercentage(completedQuizzes, totalQuizzes);
  };

  const getAverageScore = () => {
    if (quizScores.length === 0) return 0;
    const totalScore = quizScores.reduce((sum, score) => sum + score.score, 0);
    return Math.round(totalScore / quizScores.length);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-8">
        <div className="text-error-600 mb-4">{error}</div>
        <button onClick={loadDashboardData} className="btn-primary">
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Welcome Section */}
      <div className="bg-gradient-to-r from-primary-600 to-accent-600 rounded-xl p-6 text-white">
        <h1 className="text-2xl font-bold mb-2">
          Welcome back, {profile?.full_name || 'Student'}!
        </h1>
        <p className="text-primary-100">
          Continue your project management journey with our comprehensive training program.
        </p>
      </div>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="card">
          <div className="flex items-center">
            <div className="p-2 bg-primary-100 rounded-lg">
              <BookOpen className="h-6 w-6 text-primary-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Enrolled Courses</p>
              <p className="text-2xl font-bold text-gray-900">{enrolledCourses.length}</p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center">
            <div className="p-2 bg-success-100 rounded-lg">
              <CheckCircle className="h-6 w-6 text-success-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Completed Quizzes</p>
              <p className="text-2xl font-bold text-gray-900">{quizScores.length}</p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center">
            <div className="p-2 bg-accent-100 rounded-lg">
              <BarChart3 className="h-6 w-6 text-accent-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Overall Progress</p>
              <p className="text-2xl font-bold text-gray-900">{getOverallProgress()}%</p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center">
            <div className="p-2 bg-yellow-100 rounded-lg">
              <Award className="h-6 w-6 text-yellow-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Average Score</p>
              <p className="text-2xl font-bold text-gray-900">{getAverageScore()}%</p>
            </div>
          </div>
        </div>
      </div>

      {/* Enrolled Courses */}
      <div className="card">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-bold text-gray-900">Your Enrolled Courses</h2>
          <Link to="/dashboard/courses" className="text-primary-600 hover:text-primary-700 font-medium">
            View All
          </Link>
        </div>
        
        {enrolledCourses.length === 0 ? (
          <div className="text-center py-8">
            <BookOpen className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No courses enrolled yet</h3>
            <p className="text-gray-600 mb-4">Start your learning journey by enrolling in our project management courses.</p>
            <Link to="/dashboard/courses" className="btn-primary">
              Browse Courses
            </Link>
          </div>
        ) : (
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
            {enrolledCourses.map((enrollment) => (
              <div key={enrollment.id} className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow">
                <div className="flex items-start justify-between mb-3">
                  <h3 className="font-semibold text-gray-900">{enrollment.courses?.title}</h3>
                  <span className="text-xs bg-primary-100 text-primary-700 px-2 py-1 rounded-full">
                    {enrollment.courses?.level}
                  </span>
                </div>
                <p className="text-sm text-gray-600 mb-4">{enrollment.courses?.description}</p>
                <div className="flex items-center justify-between">
                  <div className="flex items-center text-sm text-gray-500">
                    <Clock className="h-4 w-4 mr-1" />
                    {enrollment.courses?.duration}
                  </div>
                  <Link 
                    to={`/course/${enrollment.course_id}`}
                    className="text-primary-600 hover:text-primary-700 text-sm font-medium"
                  >
                    Continue Learning
                  </Link>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Weekly Quizzes */}
      <div className="card">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-bold text-gray-900">Weekly Quizzes</h2>
          <Link to="/dashboard/quizzes" className="text-primary-600 hover:text-primary-700 font-medium">
            View All
          </Link>
        </div>
        
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
          {Array.from({ length: 12 }, (_, i) => i + 1).map((week) => {
            const score = getQuizProgress(week);
            const isLocked = isQuizLocked(week);
            
            return (
              <div key={week} className="border border-gray-200 rounded-lg p-4">
                <div className="flex items-center justify-between mb-3">
                  <h3 className="font-semibold text-gray-900">Week {week}</h3>
                  {isLocked ? (
                    <Lock className="h-5 w-5 text-gray-400" />
                  ) : score !== null ? (
                    <CheckCircle className="h-5 w-5 text-success-500" />
                  ) : (
                    <Play className="h-5 w-5 text-primary-500" />
                  )}
                </div>
                
                <div className="mb-3">
                  {score !== null ? (
                    <div className="flex items-center">
                      <span className="text-2xl font-bold text-success-600">{score}%</span>
                      <span className="text-sm text-gray-500 ml-2">Score</span>
                    </div>
                  ) : (
                    <div className="text-sm text-gray-500">
                      {isLocked ? 'Complete previous week first' : 'Not attempted'}
                    </div>
                  )}
                </div>
                
                <Link
                  to={`/quiz/${week}`}
                  className={`block w-full text-center py-2 px-4 rounded-lg text-sm font-medium transition-colors ${
                    isLocked
                      ? 'bg-gray-100 text-gray-400 cursor-not-allowed'
                      : 'bg-primary-600 hover:bg-primary-700 text-white'
                  }`}
                  onClick={(e) => isLocked && e.preventDefault()}
                >
                  {isLocked ? 'Locked' : score !== null ? 'Review' : 'Start Quiz'}
                </Link>
              </div>
            );
          })}
        </div>
      </div>

      {/* Recent Activity */}
      <div className="card">
        <h2 className="text-xl font-bold text-gray-900 mb-6">Recent Activity</h2>
        
        {quizScores.length === 0 ? (
          <div className="text-center py-8">
            <FileText className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-600">No recent activity. Start with your first quiz!</p>
          </div>
        ) : (
          <div className="space-y-4">
            {quizScores.slice(0, 5).map((score) => (
              <div key={score.id} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                <div className="flex items-center">
                  <div className="p-2 bg-primary-100 rounded-lg mr-4">
                    <FileText className="h-5 w-5 text-primary-600" />
                  </div>
                  <div>
                    <p className="font-medium text-gray-900">
                      Week {score.quizzes?.week_number} Quiz
                    </p>
                    <p className="text-sm text-gray-500">
                      Completed on {formatDate(score.submitted_at)}
                    </p>
                  </div>
                </div>
                <div className="text-right">
                  <span className="text-lg font-bold text-success-600">{score.score}%</span>
                  <p className="text-sm text-gray-500">Score</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Quick Actions */}
      <div className="grid md:grid-cols-2 gap-6">
        <div className="card">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Download Resources</h3>
          <p className="text-gray-600 mb-4">Access study materials, templates, and reference guides.</p>
          <button className="btn-secondary w-full">
            <Download className="h-5 w-5 mr-2" />
            Download All Resources
          </button>
        </div>

        <div className="card">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Upcoming Events</h3>
          <p className="text-gray-600 mb-4">Join live workshops and certification prep sessions.</p>
          <button className="btn-secondary w-full">
            <Calendar className="h-5 w-5 mr-2" />
            View Schedule
          </button>
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
