import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const CoursesPage = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [activeFilter, setActiveFilter] = useState('Popular');

  const filters = ['Popular', 'New', 'Certified'];
  const categories = ['Project Management', 'Agile', 'Scrum', 'Kanban', 'Lean', 'Six Sigma'];
  const filterOptions = ['Format', 'Level', 'Language'];

  const courses = [
    {
      id: 1,
      title: 'Project Management Fundamentals',
      price: '$49',
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuBfV9preMTmmTsF2jvvR6PbEp90slIEcjRFxUh9ucwDn3SovN3UhqEF_n522A49oHq3Bb2TuapgYCTcI6YMtsiNY3WeiL1qT45NjTVUzXQg_GWZ3CQ4DaGw95LJLplgAtK9Yls9aJFwz1LgedASM63WB2fDnIE722qdXHfAfb0jC-KWY7H4D1ZT1MLQPxrf6WC28zRoyDQ7FELc-5zvJ37DG8FQR2jH3BchCSRu3L34CRSEWQeCDNZPLmmvJo73Bxub0XVvdmRTBSY'
    },
    {
      id: 2,
      title: 'Agile Project Management',
      price: '$79',
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuDFJbKJn97bhsAkkbvasPvQUu_zebPNCdN0q7THfYOW6j1dXcaNuCcND3JwR9UBMAhdFVEGp-ci_7Zu7M_L3aPZLlq5z9jBFKmvxTUtL50c20mRu8Sr-Nj9Zgo17GrTLYJPqzv5UKynUjygMov_ILAsK9uQYGUT_KuWfdlAzCZepHv7CU06lrtxPGyJZY0dFaGDAcuR1mtQOL4VmbaAsRVQ9vLhhtD4UlDnD0hsiGzKP1d2wpxhijGuNjQ577UZ2QZuq826dCv-6GA'
    },
    {
      id: 3,
      title: 'Scrum Master Certification',
      price: '$99',
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuDUQQThd2D3_QRfwb8ccGri0snG86WuYW1y_w4XwxgW2hjvBLsORCnC5E3a_6Vjfx85NDPRQRMFOSlUXQdKG90uZ8B1ogln8fl4U6UdRJrYvl-hG35382FAhfc8_TsOT8cIjqM8598trdOL2GEaatvF1PcmmcfrCqH8Uq9BfATDnXxPcSXrAhaCsyEUB76uCHG5WGQgo1Os-5FtP_1n6o0NqzA5Y8nObCGMMslD4xpb7dWjfAHWstihvic-T0Oprp7di-WZqK3iyUM'
    },
    {
      id: 4,
      title: 'Kanban for Beginners',
      price: '$39',
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuBf-d9jHjFwpTeb0eqEI8ObwX6Hnv-luKHiY1_4jr7cdeOVStHnYVrknBPZqK0M_N3PaA7mgYjmEK6i87K5TsdBjGAtFy0qfOj8m0MqRc3E3sXFPgVQUFwmdjprY98Qxn7bx2D7-Y-P6_rc71t7rOfdivMHSjyT4g32wiAdKQEKKbqlsbeZLe71EifZz7KusWwB01JFoYTF6zGIMjG9yJh-qDf8VDmblwo4f14Q18KnAVCZsHfNFfjfpaOqoBpHOyEndhzD9pJccmw'
    },
    {
      id: 5,
      title: 'Lean Six Sigma Green Belt',
      price: '$129',
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuBoCGf--oqgJyGXbHvMMHxAwluWErSaU7LGwLK5YEznguKw0XAsrAorNBkegC5DvWLcKBd2QU9YQXHKXrhgCxkEZO0Z-QZlZ_QOPRp7SRx78aZm_9v6Rjx-VSrC59bk_JevOdtIUEFK5FDjyVvHZX3DdNaW65lR7FW4DG3xREbsiDVtqbuh_7zYJOR81BSE_kx-jCTzDRPYHwlkQ3SOje3Qcq8SUrvfWbnFike1Xg9-EjeepchTJt68ORp9fTV-Pce9_9bTSsAO7Yg'
    },
    {
      id: 6,
      title: 'Advanced Agile Techniques',
      price: '$89',
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuBYUFwm2myVGFtntebV33APubeJzmXuABWRb51m86pMq5pstnBbzExPTbPYKnUE3b7RS_vL5piZOEdUwcsgmALtnDLzp4mSxliFhiBivRgitt75D98nQ_3oMBMiq6TVuSGK4bjytLT4wKjGUP3jhie1qdDt888UkrS2iOyqhQQGBeGVZDWfGMruLx2zomiO0b7-dy7AwsKN6pncGOP8d1TQL6A2ihcXchb_cLjkQSfChQ2-shggySmQxEOIsk_XmRbSEofBQrNlGns'
    }
  ];

  return (
    <div className="relative flex size-full min-h-screen flex-col bg-white justify-between group/design-root overflow-x-hidden">
      <div>
        {/* Header */}
        <div className="flex items-center bg-white p-4 pb-2 justify-between">
          <div 
            className="text-[#111418] flex size-12 shrink-0 items-center cursor-pointer"
            onClick={() => navigate(-1)}
          >
            <svg xmlns="http://www.w3.org/2000/svg" width="24px" height="24px" fill="currentColor" viewBox="0 0 256 256">
              <path d="M224,128a8,8,0,0,1-8,8H59.31l58.35,58.34a8,8,0,0,1-11.32,11.32l-72-72a8,8,0,0,1,0-11.32l72-72a8,8,0,0,1,11.32,11.32L59.31,120H216A8,8,0,0,1,224,128Z"></path>
            </svg>
          </div>
          <h2 className="text-[#111418] text-lg font-bold leading-tight tracking-[-0.015em] flex-1 text-center pr-12">Courses</h2>
        </div>

        {/* Search Bar */}
        <div className="px-4 py-3">
          <label className="flex flex-col min-w-40 h-12 w-full">
            <div className="flex w-full flex-1 items-stretch rounded-lg h-full">
              <div className="text-[#637588] flex border-none bg-[#f0f2f4] items-center justify-center pl-4 rounded-l-lg border-r-0">
                <svg xmlns="http://www.w3.org/2000/svg" width="24px" height="24px" fill="currentColor" viewBox="0 0 256 256">
                  <path d="M229.66,218.34l-50.07-50.06a88.11,88.11,0,1,0-11.31,11.31l50.06,50.07a8,8,0,0,0,11.32-11.32ZM40,112a72,72,0,1,1,72,72A72.08,72.08,0,0,1,40,112Z"></path>
                </svg>
              </div>
              <input
                placeholder="Search courses"
                className="form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-lg text-[#111418] focus:outline-0 focus:ring-0 border-none bg-[#f0f2f4] focus:border-none h-full placeholder:text-[#637588] px-4 rounded-l-none border-l-0 pl-2 text-base font-normal leading-normal"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            </div>
          </label>
        </div>

        {/* Quick Filters */}
        <div className="flex gap-3 p-3 overflow-x-hidden">
          {filters.map((filter) => (
            <div
              key={filter}
              className={`flex h-8 shrink-0 items-center justify-center gap-x-2 rounded-lg pl-4 pr-4 cursor-pointer ${
                activeFilter === filter ? 'bg-[#1672ce] text-white' : 'bg-[#f0f2f4] text-[#111418]'
              }`}
              onClick={() => setActiveFilter(filter)}
            >
              <p className="text-sm font-medium leading-normal">{filter}</p>
            </div>
          ))}
        </div>

        {/* Categories */}
        <h3 className="text-[#111418] text-lg font-bold leading-tight tracking-[-0.015em] px-4 pb-2 pt-4">Categories</h3>
        <div className="flex gap-3 p-3 flex-wrap pr-4">
          {categories.map((category) => (
            <div key={category} className="flex h-8 shrink-0 items-center justify-center gap-x-2 rounded-lg bg-[#f0f2f4] pl-4 pr-4 cursor-pointer">
              <p className="text-[#111418] text-sm font-medium leading-normal">{category}</p>
            </div>
          ))}
        </div>

        {/* Filters */}
        <h3 className="text-[#111418] text-lg font-bold leading-tight tracking-[-0.015em] px-4 pb-2 pt-4">Filters</h3>
        <div className="flex gap-3 p-3 flex-wrap pr-4">
          {filterOptions.map((option) => (
            <button key={option} className="flex h-8 shrink-0 items-center justify-center gap-x-2 rounded-lg bg-[#f0f2f4] pl-4 pr-2">
              <p className="text-[#111418] text-sm font-medium leading-normal">{option}</p>
              <div className="text-[#111418]">
                <svg xmlns="http://www.w3.org/2000/svg" width="20px" height="20px" fill="currentColor" viewBox="0 0 256 256">
                  <path d="M213.66,101.66l-80,80a8,8,0,0,1-11.32,0l-80-80A8,8,0,0,1,53.66,90.34L128,164.69l74.34-74.35a8,8,0,0,1,11.32,11.32Z"></path>
                </svg>
              </div>
            </button>
          ))}
        </div>

        {/* Course Grid */}
        <div className="grid grid-cols-[repeat(auto-fit,minmax(158px,1fr))] gap-3 p-4">
          {courses.map((course) => (
            <Link key={course.id} to={`/course/${course.id}`} className="flex flex-col gap-3 pb-3">
              <div
                className="w-full bg-center bg-no-repeat aspect-video bg-cover rounded-lg"
                style={{ backgroundImage: `url("${course.image}")` }}
              ></div>
              <div>
                <p className="text-[#111418] text-base font-medium leading-normal">{course.title}</p>
                <p className="text-[#637588] text-sm font-normal leading-normal">{course.price}</p>
              </div>
            </Link>
          ))}
        </div>
      </div>

      {/* Bottom Navigation */}
      <div>
        <div className="flex gap-2 border-t border-[#f0f2f4] bg-white px-4 pb-3 pt-2">
          <Link to="/" className="just flex flex-1 flex-col items-center justify-end gap-1 text-[#637588]">
            <div className="text-[#637588] flex h-8 items-center justify-center">
              <svg xmlns="http://www.w3.org/2000/svg" width="24px" height="24px" fill="currentColor" viewBox="0 0 256 256">
                <path d="M218.83,103.77l-80-75.48a1.14,1.14,0,0,1-.11-.11,16,16,0,0,0-21.53,0l-.11.11L37.17,103.77A16,16,0,0,0,32,115.55V208a16,16,0,0,0,16,16H96a16,16,0,0,0,16-16V160h32v48a16,16,0,0,0,16,16h48a16,16,0,0,0,16-16V115.55A16,16,0,0,0,218.83,103.77ZM208,208H160V160a16,16,0,0,0-16-16H112a16,16,0,0,0-16,16v48H48V115.55l.11-.1L128,40l79.9,75.43.11.1Z"></path>
              </svg>
            </div>
            <p className="text-[#637588] text-xs font-medium leading-normal tracking-[0.015em]">Home</p>
          </Link>
          <Link to="/courses" className="just flex flex-1 flex-col items-center justify-end gap-1 rounded-full text-[#111418]">
            <div className="text-[#111418] flex h-8 items-center justify-center">
              <svg xmlns="http://www.w3.org/2000/svg" width="24px" height="24px" fill="currentColor" viewBox="0 0 256 256">
                <path d="M232,208a8,8,0,0,1-8,8H32a8,8,0,0,1,0-16H224A8,8,0,0,1,232,208Zm0-152V168a16,16,0,0,1-16,16H40a16,16,0,0,1-16-16V56A16,16,0,0,1,40,40H216A16,16,0,0,1,232,56Zm-68,56a8,8,0,0,0-3.71-6.75l-44-28A8,8,0,0,0,104,84v56a8,8,0,0,0,12.29,6.75l44-28A8,8,0,0,0,164,112Z"></path>
              </svg>
            </div>
            <p className="text-[#111418] text-xs font-medium leading-normal tracking-[0.015em]">Courses</p>
          </Link>
          <Link to="/dashboard" className="just flex flex-1 flex-col items-center justify-end gap-1 text-[#637588]">
            <div className="text-[#637588] flex h-8 items-center justify-center">
              <svg xmlns="http://www.w3.org/2000/svg" width="24px" height="24px" fill="currentColor" viewBox="0 0 256 256">
                <path d="M216,40H136V24a8,8,0,0,0-16,0V40H40A16,16,0,0,0,24,56V176a16,16,0,0,0,16,16H79.36L57.75,219a8,8,0,0,0,12.5,10l29.59-37h56.32l29.59,37a8,8,0,1,0,12.5-10l-21.61-27H216a16,16,0,0,0,16-16V56A16,16,0,0,0,216,40Zm0,136H40V56H216V176ZM104,120v24a8,8,0,0,1-16,0V120a8,8,0,0,1,16,0Zm32-16v40a8,8,0,0,1-16,0V104a8,8,0,0,1,16,0Zm32-16v56a8,8,0,0,1-16,0V88a8,8,0,0,1,16,0Z"></path>
              </svg>
            </div>
            <p className="text-[#637588] text-xs font-medium leading-normal tracking-[0.015em]">Dashboard</p>
          </Link>
          <Link to="/profile" className="just flex flex-1 flex-col items-center justify-end gap-1 text-[#637588]">
            <div className="text-[#637588] flex h-8 items-center justify-center">
              <svg xmlns="http://www.w3.org/2000/svg" width="24px" height="24px" fill="currentColor" viewBox="0 0 256 256">
                <path d="M230.92,212c-15.23-26.33-38.7-45.21-66.09-54.16a72,72,0,1,0-73.66,0C63.78,166.78,40.31,185.66,25.08,212a8,8,0,1,0,13.85,8c18.84-32.56,52.14-52,89.07-52s70.23,19.44,89.07,52a8,8,0,1,0,13.85-8ZM72,96a56,56,0,1,1,56,56A56.06,56.06,0,0,1,72,96Z"></path>
              </svg>
            </div>
            <p className="text-[#637588] text-xs font-medium leading-normal tracking-[0.015em]">Profile</p>
          </Link>
        </div>
        <div className="h-5 bg-white"></div>
      </div>
    </div>
  );
};

export default CoursesPage;
